using System.Collections.Generic;
using System.Linq;
using System.Net;
using System.Net.Http;
using RichardSzalay.MockHttp;

namespace PodcastManager.FeedUpdater.Doubles.HttpClientFactory;

public class HttpClientFactoryStub : HttpClientFactoryDummy
{
    public override HttpClient CreateClient(string name)
    {
        var mock = new MockHttpMessageHandler();

        foreach (var url in Urls)
            mock.When(url)
                .Respond(HttpStatusCode.OK, new StringContent(Data[url]));

        return mock.ToHttpClient();
    }

    public static IList<string> Urls => Data.Keys.ToList();

    public static Dictionary<string, string> Data { get; } = new()
